package Lec1;

public class Logical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b = 10;
		System.out.println(((a==5)&&(a>b)));
		System.out.println(((a==5)&&(a<b)));
		
		System.out.println(((a==5)||(a>b)));
		System.out.println(((a!=5)||(a<b)));
		
		System.out.println(!(a==10));
		
		
	}

}
